import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux'
import { firestoreConnect, reduxFirebase } from 'react-redux-firebase'
import { compose } from 'redux';

class UserDetails extends Component {
    render(){
        const {user} = this.props
        console.log(user);
        return(
            <div className="container">
                <Link to='/'>‚Üê Back to Dashboard</Link>
                <h2>User Details</h2>
                <div className="row">
                    <div>First Name:</div>
                    <div>Joshua</div>
                </div>
                <div className="row">
                    <div>Last Name:</div>
                    <div>Singh</div>
                </div>
                <div className="row">
                    <div>Address 1:</div>
                    <div>7, Canada Drive, Texas Road</div>
                </div>
                <div className="row">
                    <div>Address 2:</div>
                    <div>Nevada, USA</div>
                </div>
                <div className="row">
                    <div>Town:</div>
                    <div>Nevada</div>
                </div>
                <div className="row">
                    <div>Region:</div>
                    <div>South</div>
                </div>
                <div className="row">
                    <div>Country:</div>
                    <div>USA</div>
                </div>
                <div className="row">
                    <div>Post Code:</div>
                    <div>NVD 123</div>
                </div>
                <div className="row">
                    <div>Contact Number:</div>
                    <div>99123123</div>
                </div>
                <br />
                <button>Edit User</button>
            </div>
        )
    }
}

const mapStateToProps = (state, ownProps) => {
    const id = ownProps.match.params[0];
    const users = state.firestore.data.users;
    const user = users ? users[id] : null;
    return {
        user: user
    }
}

export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        { collection: 'users' }
    ])
)(UserDetails)
