import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux'
import { firestoreConnect, reduxFirebase } from 'react-redux-firebase'
import { compose } from 'redux';
import { deleteUser, updateUser } from '../../store/actions/userActions'

class EditDetails extends Component {
    deleteUser = (e) => {
        e.preventDefault();
        this.props.deleteUser(this.props.match.params[0]);
        this.props.history.push("/");
    }

    handleSubmit = (e) => {
        e.preventDefault();
        var formData = new FormData(e.target)
        var formObj = {};
        formData.forEach((value, key) => {formObj[key] = value});

        this.props.updateUser(this.props.match.params[0], formObj);
    }

    render(){
        console.log(this.props.match.params[0]);
        const { user } = this.props

        if (user) {
            return(
                <div className="container">
                    <Link to='/'>← Back to Dashboard</Link>
                    <h2>Edit User:</h2>
                    <form  onSubmit={this.handleSubmit}>
                        <div className="row">
                            <label>First Name:</label>
                            <input value={user.first_name} />
                        </div>
                        <div className="row">
                            <label>Last Name:</label>
                            <input value={user.last_name} />
                        </div>
                        <div className="row">
                            <label>Address 1:</label>
                            <input value={user.address_1} />
                        </div>
                        <div className="row">
                            <label>Address 2:</label>
                            <input value={user.address_2} />
                        </div>
                        <div className="row">
                            <label>Town:</label>
                            <input value={user.town} />
                        </div>
                        <div className="row">
                            <label>Region:</label>
                            <input value={user.region} />
                        </div>
                        <div className="row">
                            <label>Country:</label>
                            <input value={user.country} />
                        </div>
                        <div className="row">
                            <label>Post Code:</label>
                            <input value={user.post_code} />
                        </div>
                        <div className="row">
                            <label>Contact Number:</label>
                            <input value={user.contact_number} />
                        </div>
                        <br />
                        <button type="submit">Update Details</button>
                    </form>
                    <button onClick={this.deleteUser}>Delete User</button>
                </div>
            )
        } else {
            return(
                <div className="container">
                    <h1>Loading...</h1>
                </div>
            )
        }
    }
}

const mapStateToProps = (state, ownProps) => {
    const id = ownProps.match.params[0];
    const users = state.firestore.data.users;
    const user = users ? users[id] : null;
    return {
        user: user
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        deleteUser: (user) => dispatch(deleteUser(user)),
        updateUser: (user, data) => dispatch(updateUser(user, data))
    }
}

export default compose(
    connect(mapStateToProps, mapDispatchToProps),
    firestoreConnect([
        { collection: 'users' }
    ])
)(EditDetails)
