import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux'
import { firestoreConnect, reduxFirebase } from 'react-redux-firebase'
import { compose } from 'redux';

class EditDetails extends Component {
    deleteUser = (e) => {
        e.preventDefault();
        const userId = e.target.dataset.id;
        this.props.deleteUser(userId)
    }

    render(){
        console.log(this.props);
        const { user } = this.props

        if (user) {
            return(
                <div className="container">
                    <Link to='/'>← Back to Dashboard</Link>
                    <h2>Edit User:</h2>
                    <div className="row">
                        <div>First Name:</div>
                        <input value={user.first_name} />
                    </div>
                    <div className="row">
                        <div>Last Name:</div>
                        <input value={user.last_name} />
                    </div>
                    <div className="row">
                        <div>Address 1:</div>
                        <input value={user.address_1} />
                    </div>
                    <div className="row">
                        <div>Address 2:</div>
                        <input value={user.address_2} />
                    </div>
                    <div className="row">
                        <div>Town:</div>
                        <input value={user.town} />
                    </div>
                    <div className="row">
                        <div>Region:</div>
                        <input value={user.region} />
                    </div>
                    <div className="row">
                        <div>Country:</div>
                        <input value={user.country} />
                    </div>
                    <div className="row">
                        <div>Post Code:</div>
                        <input value={user.post_code} />
                    </div>
                    <div className="row">
                        <div>Contact Number:</div>
                        <input value={user.contact_number} />
                    </div>
                    <br />
                    <button>Delete User</button>
                    
                </div>
            )
        } else {
            return(
                <div className="container">
                    <h1>Loading...</h1>
                </div>
            )
        }
    }
}

const mapStateToProps = (state, ownProps) => {
    const id = ownProps.match.params[0];
    const users = state.firestore.data.users;
    const user = users ? users[id] : null;
    return {
        user: user
    }
}

export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        { collection: 'users' }
    ])
)(EditDetails)
